import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  username : string = '';
  password : string = '';
  role : string = '';
  roles : string[];

  constructor(){
    this.roles=[
      'admin',
      'user'
    ]
  }
  ngOnInit(): void {

  }
  login(){
    console.log(this.username+" "+this.password+" "+this.role)
  }

}
<div class="container-fluid">
    <div class="row">


        <div class="col-md-6">
            <img src="../../../assets/login_img.img" height="100%" width="100%">

        </div>
        <div class="col-md-4"> 
            <h3 class="test text-center">welcome to ust </h3>
            <form action="" class="mt-4">
                <h5 class="text-primary mb-3">Login </h5>
                <div class="form-floating mb-3">
                    <input type="text" class="form-control" id="floatingInput" placeholder="Username" name="username" [(ngModel)]="username" >
                    <label for="floatingInput">Email address</label>
                </div>
                  <div class="form-floating mb-3">
                    <input type="password" class="form-control" id="floatingPassword" placeholder="Password" name="password" [(ngModel)]="password" >
                    <label for="floatingPassword">Password</label>
                  </div>
                  <div class="form-floating mb-3">
                    <select class="form-select" id="floatingSelect" aria-label="Role" name="role" [(ngModel)]="role">
                      <option *ngFor="let r of roles" [value]="r">{{r}}</option>
                    </select>
                    <label for="floatingSelect">Role</label>
                  </div>
                  <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                    <button class="btn btn-outline-success me-md-2" type="button" (click)="login()" >Login</button>
                  </div>
                  <a routerLink="/signup" class="text-primary" style="text-decoration: none;">Need account ? </a>
            </form>
        </div>
        <div class="col-md-2">
      </div>
    </div>
</div>
